import React, { useEffect } from 'react';
import $ from 'jquery';
import { Carousel, Image } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import lorraine from "../assets/Lorraine_carosel_pic.png"

const Carousels = () => {
 

  let currentIndex = 0;
  const totalSlides = document.querySelectorAll('.carouselslides').length;
  const carousel = document.querySelector('.carousel');

  function nextSlide() {
    currentIndex = (currentIndex + 1) % totalSlides;
    updateCarousel();
  }

  function prevSlide() {
    currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
    updateCarousel();
  }

  function updateCarousel() {
    const translateValue = -currentIndex * 270; // Adjust the value based on card width + margin
    carousel.style.transform = `translateX(${translateValue}px)`;
  }

  return (
    <div className='carousel'>
      <div className='carouselslides'>
        <div className='carouselcard'>
          <div className='imgContainer'>
              <img src={lorraine} /> 
          </div>
          <div className='txtContainer'>
              <h5>Lorraine Bourke Byrne</h5>
              <p>Lorraine is one of the hard working founders of Lorka Makeup and Beauty Training. * whatever text is needed *</p>
          </div>
        </div>
        <div className='caroselcard'>
          <div className='imgContainer'>
              <img src={lorraine} /> 
          </div>
          <div className='txtContainer'>
              <h5>Lorraine Bourke Byrne</h5>
              <p>Lorraine is one of the hard working founders of Lorka Makeup and Beauty Training. * whatever text is needed *</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Carousels;
