import React, { useEffect } from 'react';
import { Carousel, Image } from 'react-bootstrap';
import lorraine from "../assets/Lorraine_carosel_pic.png"
import "./Carosel.css"
const Carousels = () => {
 

  let currentIndex = 0;
  const totalSlides = document.querySelectorAll('.carouselslides').length;
  const carousel = document.querySelector('.carousel');

  function nextSlide() {
    currentIndex = (currentIndex + 1) % totalSlides;
    updateCarousel();
  }

  function prevSlide() {
    currentIndex = (currentIndex - 1 + totalSlides) % totalSlides;
    updateCarousel();
  }

  function updateCarousel() {
    const translateValue = -currentIndex * 270; // Adjust the value based on card width + margin
    carousel.style.transform = `translateX(${translateValue}px)`;
  }

  return (
    <div class="card-style">
  <div class="card">
    <img src="https://i.posting.cc/t4w5js/img-01.png" alt="Image for card"/>
    <div class="card-content">
      <h2>Lorem ipsum dolor sit amet</h2>
      <p>Architecto, hic? Magnam eum error saepe dolaribus corrupti repellat quisquam alias doloreeque!</p>
      <a href="#">Read More</a>
    </div>
  </div>
</div>
  );
};

export default Carousels;
